import React from 'react'
import {useQuery, gql} from "@apollo/client";
import { Link } from "react-router-dom";

const QUERY_Tickets = gql`
 query{
  allTicket{
    firstname,
    lastname,
    phone,
    routes{
      name
    }
    departureTime,
    seat
  }
}
`;
function Ticketlist() {
	const { data, loading, error } = useQuery(
   QUERY_Ticket, {
      pollInterval: 500 // refetch the result every 0.5 second
    }
  );
	return (
		<div>
		<Link to="/Createticket">Add ticket</Link>
			<table>
        	<tr>
        		<th>firstname</th>
        		<th>lastname</th>
            <th>phone</th>
            <th>routes</th>
            <th>departure time</th>
            <th>seat</th>
        	</tr>
          {data.allTickets.map(ticket=>{
            return <tr>
            <td>{ticket.firstname}</td>
            <td>{ticket.lastname}</td>
            <td>{ticket.phone}</td>
            <td>{ticket.routes}</td>
            <td>{ticket.departure_time}</td>
            <td>{ticket.seat}</td>
            <td>
              <Link to={`/Updateticket/${ticket.id}`}>
                 update
              </Link>
            </td>
            <td>
               <Link to={`/Deleteticket/${ticket.id}`}>
                  delete
              </Link>
            </td>
          </tr>
          })}

        </table> 
			
		</div>
	)
}

export default Ticketlist
