import React, { useState } from 'react';
import { useMutation, gql } from '@apollo/client';


const AddMovie = () => {

	const CREATE_MOVIE = gql`
	mutation createMovie ($name: String!, $description: String!){
		createMovie (name: $name, description: $description){
		  id
		  name
		  description
		}
	}
	`;

	const [createmovie] = useMutation(CreateMovie, {
		variables: {
		description: formState.name,
		url: formState.description,
		}
	});
	const CreateMovie = () => {
		const [formState, setFormState] = useState({
		name: '',
		description: ''
	});
	}
	

  return (
    <div>
      <form
        onSubmit={(e) => {
        e.preventDefault();
        createMovie();
      }}
      >
        <div >
          <input
            value={formState.name}
            onChange={(e) =>
              setFormState({
                ...formState,
                description: e.target.value
              })
            }
            type="text"
            placeholder="add name of movie"
          />
          <input
            className="mb2"
            value={formState.url}
            onChange={(e) =>
              setFormState({
                ...formState,
                description: e.target.value
              })
            }
            type="text"
            placeholder="add description of movie"
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddMovie
