import React from 'react'
import { useParams} from "react-router"
import {useQuery, gql, useMutation} from "@apollo/client";

function UpdateBus() {
	
	const { id } = useParams();
	const QUERY_BUS = gql`
	query{
 		busesById(id:3){
    	id,
    	name,
    	seat
	  }
	}
	   `;
	const {data} =useQuery(QUERY_BUS,{ variables: { id } })
	const [name, setName] = useState(data.busesById.name);
	const [description, setDescription] = useState(data.busesById.description);

	return (
		<div>

			<form action="">
				<p>
					<label htmlFor="name">
						Name
					</label>
					<input type="text" name="name"  defaultValue={data.busesById.name} />
				</p>
				<p>
					<label htmlFor="seat">
						Seat
					</label>
					<input type="number" name="seat" defaultValue={data.busesById.seat}/>
				</p>
				<button type="submit">
					Submit
				</button>
			</form>
		</div>
	)
}

export default UpdateBus
