import { gql, useMutation} from "@apollo/client";
const CREATE_MOVIE = gql`
  mutation {
  	createMovie (movieData: {$name: String!, $description: String!}){
      movie{$name, 
      	$description
      }
  }
}
`;

// mutation
// {
//   createMovie(movieData:{name:"cn", description:"Mokobi"}){
//     movie{
//       name,
//       description
//     }
//   }
// }

export default function AddMovie() {
let name, description;
  const [createMovie] = useMutation(CREATE_MOVIE);

return (
    <div>
      <form
        onSubmit={e => {
          e.preventDefault();
          createMovie({ variables: {
            name: name.value,
            description: description.value


        } });
        name.value = '';
        description.value = '';
        window.location.reload();
      }}
      style = {{ marginTop: '2em', marginBottom: '2em' }}
     >
     <label>Name: </label>
     <input
       ref={node => {
         name = node;
       }}
       style={{ marginRight: '1em' }}
     />
     <label>Description: </label>
     <input
       ref={node => {
          description= node;
       }}
       style={{ marginRight: '1em' }}
     />
     
     <button type="submit" style={{ cursor: 'pointer' }}>Add a Movie</button>
    </form>
   </div>
  );}
